changeSet.createProcedure:
CREATE OR REPLACE FUNCTION json_equals(a jsonb, b jsonb) RETURNS boolean AS $$
BEGIN
    RETURN ((a IS NULL AND b IS NULL) OR (a @> b AND b @> a));
END;
$$ LANGUAGE plpgsql;

